import logo from './logo.svg';
import './App.css';
import Play from './play.js';
import playsData from './playData.json';
import { useState } from "react";

function App() {
  const [favorites, setFavorites] = useState([]);
  const [selectedGenre, setSelectedGenre] = useState('');
  const [sortedPlays, setSortedPlays] = useState([]);

  const addToFavorites = (play) => {
    setFavorites([...favorites, play]);
  };

  const removeFromFavorites = (workID) => {
    setFavorites(favorites.filter(play => play.WorkID !== workID));
  };

  const handleGenreChange = (event) => {
    setSelectedGenre(event.target.value);
  };

  const handleSortByDate = () => {
    const sortedPlays = [...playsData].sort((a, b) => {
      return new Date(a.Date) - new Date(b.Date);
    });
    setSortedPlays(sortedPlays);
  };

  const filteredPlays = playsData.filter(play => {
    if (!selectedGenre) return true; // If no genre is selected, return all plays
    return play.GenreType === selectedGenre;
  });

  const displayPlays = sortedPlays.length ? sortedPlays : filteredPlays;

  return (
    <div className="App">
      <h1>Shakespeare Library</h1>
      <div>
        <button onClick={handleSortByDate}>Sort by Year</button>
        <label htmlFor="genreFilter">Filter by Genre: </label>
        <select id="genreFilter" value={selectedGenre} onChange={handleGenreChange}>
          <option value="">All</option>
          <option value="c">Comedy</option>
          <option value="t">Tragedy</option>
          <option value="h">History</option>
          <option value="p">Poem</option>
          <option value="s">Sonnets</option>
        </select>
      </div>

      <div className="play-list">
        <div className="grid-container">
          {displayPlays.map((playData) => (
            <Play key={playData.WorkID} playData={playData} onFavorite={addToFavorites} onRemoveFromFavorites={removeFromFavorites}/>
          ))}
        </div>
      </div>

      <div className="favorites">
        <h2>Favorites</h2>
        <ul>
          {favorites.map((favorite) => (
            <li key={favorite.WorkID}>{favorite.Title}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
