import React, { useState } from 'react';
import Play from './play.js';
import playsData from './playData.json';

function App() {
  const [favorites, setFavorites] = useState([]);
  const [sortedPlays, setSortedPlays] = useState([]);
  const [selectedGenre, setSelectedGenre] = useState('');
  const [selectedType, setSelectedType] = useState('');

  const addToFavorites = (play) => {
    setFavorites([...favorites, play]);
  };

  const removeFromFavorites = (workID) => {
    setFavorites(favorites.filter(play => play.WorkID !== workID));
  };

  const handleTypeChange = (event) => {
    const selectedType = event.target.value;
    setSelectedType(selectedType);
  };

  const handleGenreChange = (event) => {
    const selectedGenre = event.target.value;
    setSelectedGenre(selectedGenre);
  };

  const filterPlays = () => {
    let filteredPlays = [...playsData];
    if (selectedGenre) {
      filteredPlays = filteredPlays.filter(play => play.GenreType === selectedGenre);
    }
    if (selectedType === 'poems') {
      filteredPlays = filteredPlays.filter(play => play.GenreType === '~p~' || play.GenreType === '~s~');
    }
    return filteredPlays;
  };

  const sortPlaysByDate = (plays) => {
    return plays.sort((a, b) => {
      return new Date(a.Date) - new Date(b.Date);
    });
  };

  const handleSortByYear = () => {
    const filteredPlays = filterPlays();
    const sortedPlays = sortPlaysByDate(filteredPlays);
    setSortedPlays(sortedPlays);
  };

  return (
    <div className="App">
      <div className="controls">
        <button onClick={handleSortByYear}>Sort by Year</button>
        <select value={selectedType} onChange={handleTypeChange}>
          <option value="">All Types</option>
          <option value="plays">Plays</option>
          <option value="poems">Poems</option>
        </select>
        <select value={selectedGenre} onChange={handleGenreChange}>
          <option value="">All Genres</option>
          <option value="~c~">Comedy</option>
          <option value="~t~">Tragedy</option>
          <option value="~h~">History</option>
        </select>
      </div>
      <div className="play-list">
        <div className="grid-container">
          {(sortedPlays.length ? sortedPlays : filterPlays()).map((playData) => (
            <Play key={playData.WorkID} playData={playData} onFavorite={addToFavorites} onRemoveFromFavorites={removeFromFavorites}/>
          ))}
        </div>
      </div>
      <div className="favorites">
        <h2>Favorites</h2>
        <ul>
          {favorites.map((favorite) => (
            <li key={favorite.WorkID}>{favorite.Title}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
