import logo from './logo.svg';
import './App.css';
import Play from './play.js';
import playsData from './playData.json';
import { useState } from "react";

const genreMapping = {
  '~c~': 'Comedy',
  '~t~': 'Tragedy',
  '~h~': 'History'
};

const typeMapping = {
  'plays': 'Plays',
  'poems': 'Poems'
};

function App() {
  const [favorites, setFavorites] = useState([]);
  const [sortedPlays, setSortedPlays] = useState([]); 
  const [selectedType, setSelectedType] = useState('');
  const [selectedGenre, setSelectedGenre] = useState('');

  const addToFavorites = (play) => {
    setFavorites([...favorites, play]);
  };

  const removeFromFavorites = (workID) => {
    setFavorites(favorites.filter(play => play.WorkID !== workID));
  };

  const handleTypeChange = (event) => {
    const selectedType = event.target.value;
    setSelectedType(selectedType);
  };

  const handleGenreChange = (event) => {
    const selectedGenre = event.target.value;
    setSelectedGenre(selectedGenre);
    console.log("Selected Genre:", selectedGenre); // Log the selected genre
  };

  const filterPlaysByType = () => {
    if (selectedType === 'plays') {
      return playsData.filter(play => !play.GenreType.includes('~p~') && !play.GenreType.includes('~s~'));
    } else if (selectedType === 'poems') {
      return playsData.filter(play => play.GenreType.includes('~p~') || play.GenreType.includes('~s~'));
    } else {
      return playsData;
    }
  };

  const filterPlaysByGenre = () => {
    if (selectedGenre === '') {
      console.log("No genre selected. Displaying all plays.");
      return playsData;
    } else {
      const filteredPlays = playsData.filter(play => play.GenreType === selectedGenre);
      console.log("Filtered Plays:", filteredPlays); // Log the filtered plays
      return filteredPlays;
    }
  };

  const sortPlaysByDate = () => {
    const sortedPlays = [...playsData].sort((a, b) => {
      return new Date(a.Date) - new Date(b.Date);
    });
    setSortedPlays(sortedPlays);
  };

  const resetFilters = () => {
    setSelectedGenre('');
    setSortedPlays([]);
  };

  return (
    <div className="App">
      <div className="content">
        <div className="left-column">
          <h1>Shakespeare Library</h1>
          <div>
            <button onClick={sortPlaysByDate}>Sort by Year</button> 
            <label htmlFor="typeFilter">Sort by Type: </label>
            <select id="typeFilter" value={selectedType} onChange={handleTypeChange}>
              <option value="">All</option>
              {Object.entries(typeMapping).map(([key, value]) => (
                <option key={key} value={key}>{value}</option>
              ))}
            </select>
            <label htmlFor="genreFilter">Sort by Genre: </label>
            <select id="genreFilter" value={selectedGenre} onChange={handleGenreChange}>
              <option value="">All</option>
              {Object.entries(genreMapping).map(([abbreviated, full]) => (
                <option key={abbreviated} value={abbreviated}>{full}</option>
              ))}
            </select>
            <button onClick={resetFilters}>Reset</button>
          </div>
          <div className="play-list">
            <div className="grid-container">
              {(sortedPlays.length ? sortedPlays : filterPlaysByGenre()).map((playData) => (
                <Play key={playData.WorkID} playData={playData} onFavorite={addToFavorites} onRemoveFromFavorites={removeFromFavorites}/>
              ))}
              {(sortedPlays.length ? sortedPlays : filterPlaysByType()).map((playData) => (
                <Play key={playData.WorkID} playData={playData} onFavorite={addToFavorites} onRemoveFromFavorites={removeFromFavorites}/>
              ))}
            </div>
          </div>
        </div>

        <div className="right-column">
          <div className="favorites">
            <h2>Favorites</h2>
            <ul>
              {favorites.map((favorite) => (
                <li key={favorite.WorkID}>{favorite.Title}</li>
              ))}
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
