import logo from './logo.svg';
import './App.css';
import Play from './play.js';
import playsData from './playData.json';

function App() {

  const [favorites, setFavorites] = useState([]);

  const addToFavorites = (play) => {
    setFavorites([...favorites, play]);
  };

  return (
    <div className="App">
      <h1>Shakespeare Library</h1>
      <div className="play-list">
        {playsData.map((playData) => (
          <Play key={playData.WorkID} playData={playData} onFavorite={addToFavorites} />
        ))}
      </div>
      <div className="favorites">
        <h2>Favorites</h2>
        <ul>
          {favorites.map((favorite) => (
            <li key={favorite.WorkID}>{favorite.Title}</li>
          ))}
        </ul>
      </div>

    </div>
  );
}

export default App;
