import logo from './logo.svg';
import './App.css';
import Play from './play.js';
import playsData from './playData.json';
import { useState } from "react";

const genreMapping = {
  '~c~': 'Comedy',
  '~t~': 'Tragedy',
  '~h~': 'History',
  '~p~': 'Poem',
  '~s~': 'Sonnets'
};

function App() {
  const [favorites, setFavorites] = useState([]);
  const [sortedPlays, setSortedPlays] = useState([]); 
  const [selectedGenre, setSelectedGenre] = useState('');

  const addToFavorites = (play) => {
    setFavorites([...favorites, play]);
  };

  const removeFromFavorites = (workID) => {
    setFavorites(favorites.filter(play => play.WorkID !== workID));
  };

  const handleGenreChange = (event) => {
    const selectedGenre = event.target.value;
    setSelectedGenre(selectedGenre);
    console.log("Selected Genre:", selectedGenre); // Log the selected genre
  };

  const filterPlaysByGenre = () => {
    if (selectedGenre === '') {
      console.log("No genre selected. Displaying all plays.");
      return playsData;
    } else {
      const filteredPlays = playsData.filter(play => play.GenreType === selectedGenre);
      console.log("Filtered Plays:", filteredPlays); // Log the filtered plays
      return filteredPlays;
    }
  };

  const sortPlaysByDate = () => {
    const sortedPlays = [...playsData].sort((a, b) => {
      return new Date(a.Date) - new Date(b.Date);
    });
    setSortedPlays(sortedPlays);
  };

  const resetFilters = () => {
    setSelectedGenre('');
    setSortedPlays([]);
  };

  return (
    <div className="App">
      <div className="content">
        <div className="left-column">
          <h1>Shakespeare Library</h1>
          <div>
            {/* Your filter and sort buttons */}
          </div>
          <div className="play-list">
            <div className="grid-container">
              {/* Render the play grid here */}
            </div>
          </div>
        </div>
        <div className="right-column">
          <div className="favorites">
            <h2>Favorites</h2>
            <ul>
              {/* Render the favorites list here */}
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
